mapper.select:
SELECT b.item_idx , b.Title , b.uidx , b.contents , date_format(b.wdate,'%y-%m-%d-%h:%i')as wdate , u.nickName , b.Price , b.image1 , b.isDel , b.state , b.keyword FROM Board_Items b, AnnaUser u WHERE b.uidx = u.uidx and b.isDel ='N'
AND ( b.title like concat ('%',#{searchVal},'%') or b.keyword like concat ('%',#{searchVal},'%') )
and cate_idx = #{cate_idx}
ORDER BY b.item_idx desc limit #{startPage}, #{pageNum}

mapper.update:
Update Board_Items Set
					cate_idx = #{cate_idx}
					,Title = #{title}
					,contents = #{contents}
					,wdate = now()
					,addr1 = #{addr1}
					,addr2 = #{addr2}
					,addr_Code = #{addr_code}
					,image1 = #{image1}
					,image2 = #{image2}
					,image3 = #{image3}
					,image4 = #{image4}
					,image5 = #{image5}
					,image6 = #{image6}
					,image7 = #{image7}
					,image8 = #{image8}
					,image9 = #{image9}
					,image10 = #{image10}
					,Keyword = #{keyword}
					,Price = #{price}
					,offer = #{offer}
					,hit = 1 
					,state = #{state}
					,isDel = 'N'

					where item_idx = #{item_idx}

update Board_Items Set
	isDel = 'Y'
where item_idx = #{item_idx}

mapper.select:
select neighbor_idx, 
(select nickName from AnnaUser where uidx = neighbor_idx ) as nickName 
from Neighbor where uidx = #{uidx}

select neighbor_idx, 
(select nickName from AnnaUser where uidx = neighbor_idx ) as nickName 
from Neighbor where uidx = #{youidx}

select COUNT(*) from Neighbor 
where uidx = #{uidx}
 and  neighbor_idx = #{neighbor_idx}

		select neighbor_idx,(
 			select nickname from AnnaUser where uidx = neighbor_idx )as nickName 
 		from Neighbor where uidx = #{uidx}

mapper.insert:
INSERT INTO chat(
		uidx
