	)
	VALUES(
		 #{uidx}
	,	 #{item_idx}
	,	 #{contents}
	,	 #{chat_host}
	,	 now()
	,	 #{invited}
	)

mapper.delete:
delete from blackList 
where uidx = #{uidx} 
and black_idx = #{black_idx}
