mapper.select:
SELECT b.item_idx , b.Title , b.uidx , b.contents , date_format(b.wdate,'%y-%m-%d-%h:%i')as wdate , u.nickName , b.Price , b.image1 , b.isDel , b.state , b.keyword , b.addr_code , b.hit , (select count(*) from Wish_list w where w.item_idx = b.item_idx and w.uidx = #{pagemaker.uidx} ) as WishCheck , (select count(uidx)as WishCount from Wish_list where item_idx = b.item_idx)as WishCount FROM Board_Items b, AnnaUser u WHERE b.uidx = u.uidx and b.isDel ='N'
AND ( b.title like concat ('%',#{pagemaker.searchVal},'%') or b.keyword like concat ('%',#{pagemaker.searchVal},'%') )
b.addr_code like concat('%',#{item},'%')
AND b.uidx = #{pagemaker.searchUidx}
and cate_idx = #{pagemaker.cate_idx}
b.keyword like concat('%',#{item},'%') and b.uidx != #{pagemaker.uidx}
and b.uidx != #{pagemaker.uidx} and b.state = 1 ORDER BY WishCount desc limit #{pagemaker.startPage}, #{pagemaker.pageNum}
ORDER BY wdate desc limit #{pagemaker.startPage}, #{pagemaker.pageNum}

SELECT count(*) FROM Board_Items b inner join AnnaUser u on b.uidx = u.uidx where b.isDel = 'N'
AND ( b.title like concat ('%',#{searchVal},'%') or b.keyword like concat ('%',#{searchVal},'%') )
AND b.uidx = #{searchUidx}
and cate_idx = #{cate_idx}

mapper.update:
Update Board_Items Set
					cate_idx = #{cate_idx}
					,Title = #{title}
					,contents = #{contents}
					,wdate = now()
					,addr1 = #{addr1}
					,addr2 = #{addr2}
					,addr_Code = #{addr_code}

					,image1 = #{image1}


					,image2 = #{image2}


					,image3 = #{image3}


					,image4 = #{image4}


					,image5 = #{image5}


					,image6 = #{image6}


					,image7 = #{image7}


					,image8 = #{image8}


					,image9 = #{image9}


					,image10 = #{image10}

					,Keyword = #{keyword}
					,Price = #{price}
					,offer = #{offer}
					,hit = 1 
					,state = #{state}
					,isDel = 'N'

					where item_idx = #{item_idx}

update Board_Items Set
	isDel = 'Y'
where item_idx = #{item_idx}

mapper.select:
select neighbor_idx, 
(select nickName from AnnaUser where uidx = neighbor_idx ) as nickName 
from Neighbor where uidx = #{uidx}

select neighbor_idx, 
(select nickName from AnnaUser where uidx = neighbor_idx ) as nickName 
from Neighbor where uidx = #{youidx}

select COUNT(*) from Neighbor 
where uidx = #{uidx}
 and  neighbor_idx = #{neighbor_idx}

		select neighbor_idx,(
 			select nickname from AnnaUser where uidx = neighbor_idx )as nickName 
 		from Neighbor where uidx = #{uidx}

mapper.insert:
