mapper.select:
SELECT b.item_idx , b.Title , b.uidx , b.Contents , b.wdate , u.nickName , b.Price , b.image1 FROM Board_Items b, AnnaUser u WHERE b.uidx = u.uidx
AND b.title like concat ('%',#{searchVal},'%')
and cate_idx = #{cate_idx}
ORDER BY b.item_idx desc limit #{startPage}, #{pageNum}

SELECT count(*) FROM Board_Items b inner join AnnaUser u on b.uidx = u.uidx where b.isDel = 'N'
AND b.title like concat ('%',#{searchVal},'%')
and cate_idx = #{cate_idx}

mapper.update:
Update Board_Items Set
					cate_idx = #{cate_idx}
					,Title = #{title}
					,Contents = #{content}
					,wdate = now()
					,addr1 = #{addr1}
					,addr2 = #{addr2}
					,addr_Code = #{addr_code}
					,image1 = #{image1}
					,image2 = #{image2}
					,image3 = #{image3}
					,image4 = #{image4}
					,image5 = #{image5}
					,image6 = #{image6}
					,image7 = #{image7}
					,image8 = #{image8}
					,image9 = #{image9}
					,image10 = #{image10}
					,Keyword = #{keyword}
					,Price = #{price}
					,offer = #{offer}
					,hit = 1 
					,state = #{state}
					,isDel = 'N'

					where item_idx = #{item_idx}

update Board_Items Set
	isDel = 'Y'
where item_idx = #{item_idx}

mapper.insert:
INSERT INTO chat(
		uidx
	,	item_idx
	,	contents
	,	chat_host
	,	cdate
	,	invited
	)
	VALUES(
		 #{uidx}
	,	 #{item_idx}
	,	 #{contents}
	,	 #{chat_host}
	,	 now()
	,	 #{invited}
	)
