mapper.update:

UPDATE 
	AnnaUser
SET
	kakao_auth = #{ kakao_auth }
WHERE
	uidx = #{ uidx }

UPDATE AnnaUser SET
interested = #{ interested }
interested = null
WHERE uidx = #{ uidx }


UPDATE
	AnnaUser
SET
	location_auth = #{ location_auth }
WHERE 
	uidx = #{ uidx }

mapper.select:
SELECT a.item_idx, a.uidx, a.cate_idx, a.title, a.contents, a.wdate, a.addr1, a.addr2, a.addr_code, a.image1, a.image2, a.image3, a.image4, a.image5, a.image6, a.image7, a.image8, a.image9, a.image10, a.keyword, a.price, a.offer, a.hit, a.state, (SELECT count(*) FROM Wish_list where item_idx = a.item_idx) as WishCount, (select count(*) from (SELECT * FROM chat b GROUP BY b.item_idx, b.chat_host, b.invited) c WHERE c.item_idx = a.item_idx) as chatCount FROM Board_Items a WHERE a.isdel = 'N' AND a.uidx != #{ uidx }
a.keyword like concat('%',#{item},'%')
LIMIT 0,15

SELECT b.bidx, b.uidx, (SELECT nickName FROM AnnaUser u where u.uidx = b.uidx) as nickName, b.board_type, b.title, b.contents, DATE_FORMAT(b.wDate,'%Y-%m-%d') as wDate, b.hit, b.image1, b.image2, b.image3, b.image4, b.image5 FROM Board b WHERE isDel = 'N' AND board_type != 'notice'
location like concat('%',#{item},'%')
ORDER BY wDate DESC LIMIT 0, 5;
